// This service worker is created by next-pwa
// It will be overwritten by the workbox-based service worker generated by next-pwa during build

self.addEventListener('install', function(event) {
  // Pre-cache the offline page
  event.waitUntil(
    caches.open('offline-cache').then(function(cache) {
      return cache.addAll([
        '/offline',
        '/icons/icon-192x192.png'
      ]);
    })
  );
  self.skipWaiting();
});

self.addEventListener('activate', function(event) {
  event.waitUntil(clients.claim());
});

self.addEventListener('fetch', function(event) {
  // For navigation requests (HTML pages)
  if (event.request.mode === 'navigate') {
    event.respondWith(
      fetch(event.request).catch(function() {
        // If offline, serve the offline page
        return caches.match('/offline');
      })
    );
  } else {
    // Default fetch handling for other resources
    event.respondWith(
      fetch(event.request).catch(function() {
        // Check if we have it in cache
        return caches.match(event.request).then(function(response) {
          // Return cached response or just fail silently
          return response || new Response(null, { status: 404 });
        });
      })
    );
  }
}); 